package com.ytempest.daydayantis.pay.alipay;/** *  * ============================================================ *  * 版权 ：好莱信息科技有限公司 (c) 2014 *  * 作者 : mowen *  * 版本 ：1.0 *  * 创建日期 ： 2014年12月9日 下午4:16:30 *  * 描述 ：支付宝支付 *  * 修订历史 ： *  * ============================================================ *  */public class Result {	public String resultStatus;	public String result;	public Result(String rawResult) {		try {			String[] resultParams = rawResult.split(";");			for (String resultParam : resultParams) {				if (resultParam.startsWith("resultStatus")) {					resultStatus = gatValue(resultParam, "resultStatus");				}				if (resultParam.startsWith("result")) {					result = gatValue(resultParam, "result");				}			}		} catch (Exception e) {			e.printStackTrace();		}	}	@Override	public String toString() {		return "resultStatus={" + resultStatus + "};result={" + result + "}";	}	private String gatValue(String content, String key) {		String prefix = key + "={";		return content.substring(content.indexOf(prefix) + prefix.length(),				content.lastIndexOf("}"));	}}